import uuid from "uuid";
import axios from 'axios';
//ACTIONS


//EDIT_EXPENSE
export const editExpense = (id, updates) => {
  return {
    type: "EDIT_EXPENSE",
    id,
    updates
  };
};

//TOTAL EXPENSES

export const addExpense = (formData) => {
  return (dispatch) => {
    axios.post('/api/expenses', formData).then((res) => {
      return dispatch({
        type: 'ADD_EXPENSE',
        payload: res.data
      })
    })
  }
};


export const fetchExpenses = () => {
  return (dispatch) => {
    axios.get('http://localhost:5000/api/expenses').then((res) => {
      return dispatch({
        type: 'FETCH',
        payload: res.data
      })
    })
  }
}
