import React, { Component } from 'react';
import axios from 'axios'
import { addExpense, fetchExpenses } from '../../redux/actions/expenses-actions';
import store from '../../redux/store'
import { connect } from 'net';
class CreateExpenses extends Component {

    state = {
        description: 'rtyty',
        amount: '',
        notes: '',

    };

    //changeinput
    onChangeInput = e => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    //OnAmountChange

    //We want to allow users to type in only numbers with decimal value so we will use a regression to achieve that
    onAmountChange = e => {
        const amount = e.target.value;

        if (amount.match(/^\d*(\.\d{0,2})?$/)) {
            this.setState(() => ({ amount }));
        }
    };

    //form submit

    onFormSubmit = e => {
        e.preventDefault();

        store.dispatch(addExpense(this.state))
    };


    // componentDidMount() {
    //     store.dispatch(fetchExpenses())
    // }

    render() {
        return (
            <div>
                <h1>Create Expenses</h1>
                <form>
                    <input
                        type="text"
                        placeholder="Description"
                        autoFocus
                        value={this.state.description}
                        name="description"
                        onChange={this.onChangeInput}
                    />

                    <input
                        type="number"
                        placeholder="amount"
                        value={this.amount}
                        name="amount"
                        onChange={this.onAmountChange}
                    />


                    <textarea
                        placeholder="Add notes (optional)"
                        value={this.notes}
                        name="notes"
                        onChange={this.onChangeInput}
                    ></textarea>

                    <button type="submit" onClick={this.onFormSubmit}>
                        Submit
          </button>
                </form>
            </div>
        );
    }
}

const mapStateToprops = (state) => {
    return {
        expenses: state
    }
}

export default connect(mapStateToprops)(CreateExpenses);
