import React, { Component } from 'react';

class CreateExpenses extends Component {

    state = {
        description: '',
        amount: '',
        notes:'',

        createdAt: this.props.expense ? moment(this.props.createdAt) : moment(),
        calendarFocused: false,
        error: ""
      };

    render() {
        return (
            <div>
                <h1>Create Expenses</h1>
                <form>
                    <input
                        type="text"
                        placeholder="Description"
                        autoFocus
                        value={description}
                        name="description"
                        onChange={this.onChangeInput}
                    />

                    <input
                        type="number"
                        placeholder="amount"
                        value={amount}
                        name="amount"
                        onChange={this.onAmountChange}
                    />

                    <SingleDatePicker
                        date={this.state.createdAt} //The current date when the app runs, we will use moment = The date to start
                        onDateChange={this.onDateChange}
                        focused={this.state.calendarFocused}
                        onFocusChange={this.onFocusChange}
                        numberOfMonths={1} //select only one thus only one calendar
                        isOutsideRange={() => false} //This allows us to view all past date and current or future
                    />

                    <textarea
                        placeholder="Add notes (optional)"
                        value={notes}
                        name="notes"
                        onChange={this.onChangeInput}
                    ></textarea>

                    <button type="submit" onClick={this.onFormSubmit}>
                        Submit
          </button>
                </form>
            </div>
        );
    }
}

export default CreateExpenses;
