//Expenses Reducers

const expensesDefaultState = {
  expenses: [],
  loading: false
}
const expenseReducer = (state = expensesDefaultState, action) => {
  switch (action.type) {
    case "ADD_EXPENSE":
      return {
        ...state,
        expenses: [action.payload, ...state.expenses]
      };

    case "FETCH":
      return {
        ...state, action
      };

    case "EDIT_EXPENSE":
      return state.map(expense => {
        if (expense.id === action.id) {
          return {
            ...expense,
            ...action.updates
          };
        } else {
          return expense;
        }
      });

    default:
      return state;
  }
};

export default expenseReducer;
